tool.command:
ls -al infile;
mkdir -p /data/input;
mkdir -p /data/output;
zip -9 /data/input/input.zip infile;
/opt/rnmr1d/exec/Rnmr1D --zip /data/input/input.zip --proccmd macrofile --outnorm PQN --cpu 8 --outdir /data/output | tee /opt/output/stdout.log;
cp -f /data/output/data_matrix.txt outfile;
